unit.interface:
public interface ImportHandler {
    /**
     * Called when a new table starts<br>
     * Note that this function is not called when the provided source doesn't have table metadata.
     *
     * @param name    VList name
     * @param columnA Column A name
     * @param columnB Column B name
     */
    void newTable(String name, String columnA, String columnB);

    /**
     * Called for a new VEntry, for last "newTable" called
     *
     * @param A list of A meanings
     * @param B list of B meanings
     * @param Tip tip
     * @param addition addition for vocable
     */
    void newEntry(List<String> A, List<String> B, String Tip, String addition);

    /**
     * Called when all entries where read
     */
    void finish();

    /**
     * Called directly before the start
     */
    void start();
}

unit.class:
public class ImportFetcherBuilder {
    private CSVCustomFormat format;
    private File source;
    private ImportHandler handler;
    private int maxEntries;
    private AlertDialog dialog;
    private ProgressBar progressBar;
    private ImportFetcher.MessageProvider messageProvider;
    private Function<Void,String> importCallback;
    private boolean logErrors = true;

    public ImportFetcherBuilder setFormat(CSVCustomFormat format) {
        this.format = format;
        return this;
    }

    public ImportFetcherBuilder setSource(File source) {
        this.source = source;
        return this;
    }

    public ImportFetcherBuilder setHandler(ImportHandler handler) {
        this.handler = handler;
        return this;
    }

    public ImportFetcherBuilder setMaxEntries(int maxEntries) {
        this.maxEntries = maxEntries;
        return this;
    }

    public ImportFetcherBuilder setDialog(AlertDialog dialog) {
        this.dialog = dialog;
        return this;
    }

    public ImportFetcherBuilder setProgressBar(ProgressBar progressBar) {
        this.progressBar = progressBar;
        return this;
    }

    public ImportFetcherBuilder setMessageProvider(ImportFetcher.MessageProvider messageProvider) {
        this.messageProvider = messageProvider;
        return this;
    }

    public ImportFetcherBuilder setImportCallback(Function<Void,String> importCallback) {
        this.importCallback = importCallback;
        return this;
    }

    public ImportFetcherBuilder setLogErrors(boolean logErrors) {
        this.logErrors = logErrors;
        return this;
    }

    /**
     * Finalize builder, create ImportFetcher
     * @return ImportFetcher
     */
    public ImportFetcher createImportFetcher() {
        if(format == null || source == null || handler == null || dialog == null
                || progressBar == null || messageProvider == null || importCallback == null)
            throw new IllegalArgumentException();
        return new ImportFetcher(format, source, handler, maxEntries, dialog, progressBar,
                messageProvider,importCallback,logErrors);
    }
}

public class GenEntryComparator extends GenericComparator<VEntry, String> {
    private final int headID;

    /**
     * Generic VEntry Comparator
     *
     * @param retrievers retrievers to use for comparision<br>
     *                   passed array order defines the comparision priority
     * @param headID     ID of entry to set on top
     */
    public GenEntryComparator(ValueRetriever[] retrievers, final int headID) {
        super(retrievers);
        this.headID = headID;
    }

    @Override
    public int compare(VEntry o1, VEntry o2) {
        if (o1.getId() == headID)
            return -1;
        if (o2.getId() == headID)
            return 1;
        return super.compare(o1, o2);
    }

    /**
     * A retriever
     */
    public static final GenericComparator.ValueRetriever retA = new ValueRetriever<VEntry, String>() {
        @Override
        public String getV(VEntry obj) {
            return obj.getAString();
        }
    };

    /**
     * B retriever
     */
    public static final GenericComparator.ValueRetriever retB = new ValueRetriever<VEntry, String>() {
        @Override
        public String getV(VEntry obj) {
            return obj.getBString();
        }
    };

    /**
     * Tip retriever
     */
    public static final GenericComparator.ValueRetriever retTip = new ValueRetriever<VEntry, String>() {
        @Override
        public String getV(VEntry obj) {
            return obj.getTip();
        }
    };
}

public class PreviewParser implements ImportHandler {
    private final static int PARSE_LIMIT = 5;
    private final List<VEntry> list;
    private int parsed_limiter = 0;
    private int tblCount = 0;
    private int rows = 0;

    public PreviewParser(List<VEntry> list) {
        this.list = list;
    }

    @Override
    public void newTable(String name, String columnA, String columnB) {
        list.add(new VEntry(columnA, columnB, name, ID_RESERVED_SKIP));
        parsed_limiter = 0;
        tblCount++;
        rows++;
    }

    @Override
    public void newEntry(List<String> A, List<String> B, String Tip, String addition) {
        rows++;
        if (parsed_limiter < PARSE_LIMIT) {
            list.add(new VEntry(A, B, Tip, "", null));
            //TODO: allow addition column
            parsed_limiter++;
        }
    }

    /**
     * Returns amount of rows detected
     *
     * @return
     */
    public int getAmountRows() {
        return rows;
    }

    @Override
    public void finish() {

    }

    @Override
    public void start() {

    }

    /**
     * Is parsed list raw data without list metadata
     *
     * @return
     */
    public boolean isRawData() {
        return tblCount == 0;
    }

    /**
     * Is parsed list a multilist
     *
     * @return
     */
    public boolean isMultiList() {
        return tblCount > 1;
    }
}

unit.comment:
/**
 * Importer, does actual importing
 */

unit.class:
public class Importer implements ImportHandler {

    private static final int BUFFER_CAPACITY = 100;

    private PreviewParser previewParser;
    private IMPORT_LIST_MODE mode;
    private VList overrideList;
    private VList currentList;
    private Database db;
    private ArrayList<VEntry> insertBuffer = new ArrayList<>(BUFFER_CAPACITY);
    private boolean ignoreEntries;

    public Importer(Context context, PreviewParser previewParser, IMPORT_LIST_MODE mode, VList overrideList) {
        if (previewParser.isRawData() && overrideList == null) {
            Log.e(TAG, "RawData without passed table!");
            throw new IllegalArgumentException("Missing table!");
        }
        this.previewParser = previewParser;
        this.mode = mode;
        this.overrideList = overrideList;
        db = new Database(context);
        ignoreEntries = false;
    }

    @Override
    public void start() {
        // raw data or single list with create flag
        if (previewParser.isRawData() || (!previewParser.isMultiList() && mode == IMPORT_LIST_MODE.CREATE)) {
            currentList = overrideList;
            db.upsertVList(currentList);
        }
    }

    @Override
    public void newTable(String name, String columnA, String columnB) {
        flushBuffer();
        ignoreEntries = false;
        VList tbl = new VList(columnA, columnB, name);
        if (previewParser.isRawData()) {
            Log.w(TAG, "New VList command on raw data list!");
        } else if (previewParser.isMultiList() || mode != IMPORT_LIST_MODE.CREATE) {
            if (VList.isIDValid(db.getSetTableID(tbl))) {
                if (mode == IMPORT_LIST_MODE.REPLACE) {
                    db.emptyList(tbl);
                } else if (mode == IMPORT_LIST_MODE.IGNORE) {
                    ignoreEntries = true;
                }
            } else {
                db.upsertVList(tbl);
            }

            currentList = tbl;
        }
    }

    @Override
    public void newEntry(List<String> A, List<String> B, String Tip, String addition) {
        if (!ignoreEntries) {
            insertBuffer.add(new VEntry(A, B, Tip, addition, currentList));
            if (insertBuffer.size() >= BUFFER_CAPACITY) {
                flushBuffer();
            }
        }
    }

    /**
     * Flushes the buffer and inserts everything
     */
    private void flushBuffer() {
        if(insertBuffer.isEmpty()){
            return;
        }
        db.upsertEntries(insertBuffer);
        insertBuffer.clear();
        insertBuffer.ensureCapacity(BUFFER_CAPACITY);
    }

    @Override
    public void finish() {
        flushBuffer();
    }

    /**
     * Import list handling mode
     */
    public enum IMPORT_LIST_MODE {
        /**
         * Replace existing list's vocables
         */
        REPLACE,
        /**
         * Add to existing lists
         */
        ADD,
        /**
         * Ignore existing lists
         */
        IGNORE,
        /**
         * Create new list
         */
        CREATE
    }
}

public class GenFileEntryComparator<V extends Comparable> extends GenericComparator<BasicFileEntry, V> {
    /**
     * Generic Comparator for BasicFileEntry
     *
     * @param retrievers retrievers to use for comparision<br>
     *                   passed array order defines the comparision priority
     */
    public GenFileEntryComparator(ValueRetriever[] retrievers) {
        super(retrievers);
    }

    /**
     * type retriever<br>
     * should be used as first retriever to guarantee correct type orders
     */
    public static final GenericComparator.ValueRetriever retType = new ValueRetriever<BasicFileEntry, Integer>() {
        @Override
        public Integer getV(BasicFileEntry obj) {
            if (obj.getTypeID() == BasicFileEntry.TYPE_DIR)
                return -1;
            if (obj.getTypeID() == BasicFileEntry.TYPE_UP)
                return -2;
            else
                return 0;
        }
    };

    /**
     * name retriever
     */
    public static final GenericComparator.ValueRetriever retName = new ValueRetriever<BasicFileEntry, String>() {
        @Override
        public String getV(BasicFileEntry obj) {
            return obj.getName();
        }
    };

    /**
     * size retriever
     */
    public static final GenericComparator.ValueRetriever retSize = new ValueRetriever<BasicFileEntry, Long>() {
        @Override
        public Long getV(BasicFileEntry obj) {
            return obj.getISize();
        }
    };
}

public class GenTableComparator extends GenericComparator<VList, String> {

    private final int headID;

    /**
     * Generic VList Comparator
     *
     * @param retrievers retrievers to use for comparision<br>
     *                   passed array order defines the comparision priority
     * @param headID     ID of table to set on top
     */
    public GenTableComparator(ValueRetriever[] retrievers, final int headID) {
        super(retrievers);
        this.headID = headID;
    }

    @Override
    public int compare(VList o1, VList o2) {
        if (o1.getId() == headID)
            return -1;
        if (o2.getId() == headID)
            return 1;
        return super.compare(o1, o2);
    }

    /**
     * Name retriever
     */
    public static final GenericComparator.ValueRetriever retName = new GenericComparator.ValueRetriever<VList, String>() {
        @Override
        public String getV(VList obj) {
            return obj.getName();
        }
    };

    /**
     * A retriever
     */
    public static final GenericComparator.ValueRetriever retA = new GenericComparator.ValueRetriever<VList, String>() {
        @Override
        public String getV(VList obj) {
            return obj.getNameA();
        }
    };

    /**
     * B retriever
     */
    public static final GenericComparator.ValueRetriever retB = new GenericComparator.ValueRetriever<VList, String>() {
        @Override
        public String getV(VList obj) {
            return obj.getNameB();
        }
    };
}

