unit.class:
public class ListActivity extends FragmentActivity implements ListPickerFragment.FinishListener {

    /**
     * Set whether multi-select is enabled or not<br>
     * Boolean expected
     */
    public static final String PARAM_MULTI_SELECT = "multiselect";
    /**
     * Param key for return of selected lists<br>
     * This key contains a {@link VList} object or a {@link List} of {@link VList}
     */
    public static final String RETURN_LISTS = "selected";
    /**
     * Pass this flag as true to call this as an deletion activity
     */
    public static final String PARAM_DELETE_FLAG = "delete";
    /**
     * Optional Param key for already selected lists, available when multiselect is set<br>
     * Expects a {@link List} of {@link VList}<br>
     *     This can be null, if nothing is selected
     */
    public static final String PARAM_SELECTED = "selected";
    /**
     * Param, if set runs in editor mode, calling EditorActivity & not returning
     */
    public static final String PARAM_RUN_EDITOR = "editorMode";
    private boolean multiselect;
    ListPickerFragment listPickerFragment;
    private boolean editorMode;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.fragment_activity);

        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);
        ActionBar ab = getSupportActionBar();
        if (ab != null) {
            ab.setDisplayHomeAsUpEnabled(true);
        }

        Intent intent = getIntent();
        // handle passed params
        multiselect = intent.getBooleanExtra(PARAM_MULTI_SELECT, false);
        boolean delete = intent.getBooleanExtra(PARAM_DELETE_FLAG, false);
        editorMode = intent.getBooleanExtra(PARAM_RUN_EDITOR,false);
        if(editorMode){
            multiselect = false;
            delete = false;
        }

        ArrayList<VList> preselected;
        if (intent.hasExtra(PARAM_SELECTED)) {
            preselected = intent.getParcelableArrayListExtra(PARAM_SELECTED);
        } else {
            preselected = new ArrayList<>();
        }

        if (savedInstanceState != null) {
            //Restore the fragment's instance
            listPickerFragment = (ListPickerFragment) getSupportFragmentManager().getFragment(savedInstanceState, ListPickerFragment.TAG);
        } else {
            listPickerFragment = ListPickerFragment.newInstance(multiselect, delete,preselected,true);
        }

        setFragment(listPickerFragment);
    }

    @Override
    protected void onSaveInstanceState(Bundle outState) {
        super.onSaveInstanceState(outState);
        //Save the fragment's instance
        getSupportFragmentManager().putFragment(outState, ListPickerFragment.TAG, listPickerFragment);
    }

    @Override
    public boolean onSupportNavigateUp() {
        onBackPressed();
        return true;
    }

    @Override
    public void onBackPressed() {
        if(editorMode) {
            super.onBackPressed();
        } else {
            Intent returnIntent = new Intent();
            setResult(Activity.RESULT_CANCELED, returnIntent);
            finish();
        }

    }

    @Override
    public void selectionFinished(ArrayList<VList> selected) {
        if(editorMode) {
            Intent myIntent = new Intent(this, EditorActivity.class);
            myIntent.putExtra(EditorActivity.PARAM_NEW_TABLE, false);
            VList lst = selected.get(0);
            myIntent.putExtra(EditorActivity.PARAM_TABLE, lst);
            this.startActivity(myIntent);
        } else {
            Intent returnIntent = new Intent();
            if (multiselect)
                returnIntent.putExtra(RETURN_LISTS, selected);
            else
                returnIntent.putExtra(RETURN_LISTS, selected.get(0));
            setResult(Activity.RESULT_OK, returnIntent);
            finish();
        }
    }

    @Override
    public void cancel() {
        onBackPressed();
    }
}

public class PermActivity extends AppCompatActivity {
    public static final int MY_PERMISSIONS_REQUEST_READ_STORAGE = 10000;
    public static final String PARAM_PERMISSION = "permissions";
    public static final String PARAM_MESSAGE = "message";
    private static final String TAG = "PermActivity";

    private String permission;
    private String message;
    private Button bRetry;

    /**
     * Check whether we have this permission or not<br>
     * should be called before this activity to check whether this is necessary
     *
     * @param context
     * @param perm
     * @return true when context has specified permission
     */
    public static boolean hasPermission(Context context, String perm) {
        return ContextCompat.checkSelfPermission(context, perm) == PackageManager.PERMISSION_GRANTED;
    }

    /**
     * Request permissions for generic context & activity
     *
     * @param context
     * @param activity
     * @param perm
     */
    private static void requestPerm(Context context, Activity activity, String perm) {
        if (!hasPermission(context, perm)) {
            // Should we show an explanation?
            if (ActivityCompat.shouldShowRequestPermissionRationale(activity,
                    perm)) {
                Log.d(TAG, "not requesting..");
            }

            // No explanation needed, we can request the permission.
            Log.d(TAG, "requesting..");
            ActivityCompat.requestPermissions(activity,
                    new String[]{perm},
                    MY_PERMISSIONS_REQUEST_READ_STORAGE);
        }
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_perm);

        if(savedInstanceState == null) {
            Intent intent = getIntent();
            // handle passed params
            permission = intent.getStringExtra(PARAM_PERMISSION);
            message = intent.getStringExtra(PARAM_MESSAGE);
        } else {
            permission = savedInstanceState.getString(PARAM_PERMISSION);
            message = savedInstanceState.getString(PARAM_MESSAGE);
        }

        if (permission == null || message == null) {
            Log.wtf(TAG, "missing parameters");
        }

        TextView tMsg = (TextView) findViewById(R.id.tPermMsg);
        tMsg.setText(message);
        bRetry = (Button) findViewById(R.id.bPermReqAgain);
        bRetry.setVisibility(View.INVISIBLE);

        //TODO: allow resource IDs as message

        requestPerm();
    }

    @Override
    protected void onSaveInstanceState(Bundle outState) {
        super.onSaveInstanceState(outState);

        outState.putString(PARAM_MESSAGE, message);
        outState.putString(PARAM_PERMISSION,permission);
    }

    /**
     * Called upon retry click
     *
     * @param view
     */
    public void onRetry(View view) {
        requestPerm();
    }

    /**
     * Wrapper around requestPerm for unique calls
     */
    private void requestPerm() {
        requestPerm(getApplicationContext(), this, permission);
    }

    @Override
    public void onRequestPermissionsResult(int requestCode, String permissions[], int[] grantResults) {
        switch (requestCode) {
            case MY_PERMISSIONS_REQUEST_READ_STORAGE: {
                //permission to read storage
                if (grantResults.length > 0
                        && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                    Intent returnIntent = new Intent();
                    setResult(Activity.RESULT_OK, returnIntent);
                    finish();
                } else { // allow for retry
                    bRetry.setVisibility(View.VISIBLE);
                }
                return;
            }
        }
    }

    /**
     * Called upon cancel pressed
     *
     * @param view
     */
    public void onCancel(View view) {
        Intent returnIntent = new Intent();
        setResult(Activity.RESULT_CANCELED, returnIntent);
        finish();
    }

}

public class FileActivity extends AppCompatActivity {
    /**
     * Param key under which the selected file is returned to the next activity<br>
     * File is passed as string containing the absolute path<br>
     * Type: File
     */
    public static final String RETURN_FILE = "file";
    /**
     * Param key for return of user friendly formated file path<br>
     * only containing the normal user-visible storage path<br>
     * Type: String
     */
    public static final String RETURN_FILE_USER_NAME = "user_file_path";
    /**
     * Param key for write flag<br>
     * Pass as true to get a save-as activity, otherwise read file "dialog"
     */
    public static final String PARAM_WRITE_FLAG = "write_flag";
    /**
     * Param key for short message to display
     */
    public static final String PARAM_MESSAGE = "message";
    /**
     * Optional param key for default file name, used upon write flag set true
     */
    public static final String PARAM_DEFAULT_FILENAME = "default_filename";
    private static final String P_KEY_FA_LAST_DIR = "last_directory";
    private static final String P_KEY_FA_LAST_FILENAME = "last_filename";
    private static final String P_KEY_FA_SORT = "FA_sorting_name";
    private static final String TAG = "FileActivity";
    private ListView listView;
    private EditText tFileName;
    private TextView tCurrentDir;
    private Button bOk;

    private ArrayList<BasicFileEntry> entries;
    private FileListAdapter adapter;
    private Formatter fmt;
    private boolean write;
    private File currentDir;
    private BasicFileEntry selectedEntry;
    private String basicDir; // user invisible part to remove
    private String defaultFileName;
    private File selectedFile;
    private boolean sorting_name;
    private Comparator<BasicFileEntry> compName;
    private Comparator<BasicFileEntry> compSize;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_file);

        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);
        ActionBar ab = getSupportActionBar();
        if(ab != null) {
            ab.setDisplayHomeAsUpEnabled(true);
        }

        compName = new GenFileEntryComparator<>(new GenericComparator.ValueRetriever[] {
                GenFileEntryComparator.retType,GenFileEntryComparator.retName,
                GenFileEntryComparator.retSize
        });
        compSize = new GenFileEntryComparator<>(new GenericComparator.ValueRetriever[] {
                GenFileEntryComparator.retType,GenFileEntryComparator.retSize,
                GenFileEntryComparator.retName
        });

        fmt = new Formatter();

        TextView msg = (TextView) findViewById(R.id.tFileMsg);
        tFileName = (EditText) findViewById(R.id.tFileName);
        tCurrentDir = (TextView) findViewById(R.id.tCurrentDir);
        bOk = (Button) findViewById(R.id.bFileOk);

        Intent intent = getIntent();
        msg.setText(intent.getStringExtra(PARAM_MESSAGE));
        write = intent.getBooleanExtra(PARAM_WRITE_FLAG, false);

        tFileName.setVisibility(write ? View.VISIBLE : View.GONE);

        String defaultName = intent.getStringExtra(PARAM_DEFAULT_FILENAME);
        defaultFileName = defaultName == null ? "file.xy" : defaultName;

        initListView();
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.file, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        switch (item.getItemId()) {
            case R.id.fMenu_sort_name:
                sorting_name = true;
                applySorting();
                return true;
            case R.id.fMenu_sort_size:
                sorting_name = false;
                applySorting();
                return true;
        }

        return super.onOptionsItemSelected(item);
    }

    /**
     * Setup listview
     */
    private void initListView() {
        listView = (ListView) findViewById(R.id.listViewFiles);
        listView.setChoiceMode(AbsListView.CHOICE_MODE_SINGLE);
        listView.setLongClickable(false);

        entries = new ArrayList<>(20); // just a good guess
        adapter = new FileListAdapter(this, entries);

        listView.setAdapter(adapter);

        listView.setOnItemClickListener((parent, view, pos, id) -> {
            BasicFileEntry entry = entries.get(pos);
            if (entry.getTypeID() == BasicFileEntry.TYPE_FILE) {
                Log.d(TAG, "selected: " + entry.getName() + " " + view.toString());
                view.setSelected(true);
                view.setActivated(true);
                selectedEntry = entry;
                bOk.setEnabled(true);
                if (write) {
                    tFileName.setText(entry.getName());
                }
            } else if (entry.getTypeID() == BasicFileEntry.TYPE_DIR) {
                currentDir = ((FileEntry) entry).getFile();
                changeDir();
            } else if (entry.getTypeID() == BasicFileEntry.TYPE_UP) {
                goUp();
            }
        });
        SharedPreferences settings = getSharedPreferences(PREFS_NAME, 0);
        sorting_name = settings.getBoolean(P_KEY_FA_SORT, true);
        setBasicDir(settings);
        changeDir();
    }

    /**
     * Does sorting<br>
     * Notifies data change
     */
    private void applySorting() {
        adapter.updateSorting(sorting_name ? compName : compSize);
    }

    /**
     * Go on directory up in navigation, if possible
     */
    private void goUp() {
        if (isRoot()) {
            Log.d(TAG, "cancel go up");
        } else {
            currentDir = currentDir.getParentFile();
            changeDir();
        }
    }

    /**
     * Check whether we're at root dir and can't go further above
     * @return true on root
     */
    private boolean isRoot(){
        return currentDir.getAbsolutePath().equals(basicDir);
    }

    /**
     * Action for Cancel button press
     *
     * @param view
     */
    public void onCancelPressed(View view) {
        cancel();
    }

    @Override
    public boolean onSupportNavigateUp() {
        cancel();
        return true;
    }

    @Override
    public void onBackPressed() {
        cancel();
    }

    /**
     * Cancel file activity
     */
    private void cancel() {
        Intent returnIntent = new Intent();
        setResult(Activity.RESULT_CANCELED, returnIntent);
        finish();
    }

    /**
     * Action for OK button press
     *
     * @param view
     */
    public void onOkPressed(View view) {
        if (write || selectedEntry != null) {
            selectedFile = write ? new File(currentDir, tFileName.getText().toString()) : ((FileEntry) selectedEntry).getFile();
            Log.d(TAG, "file:" + selectedFile.getAbsolutePath());
            if (write) {
                if (selectedFile.isDirectory()) { // required !?
                    selectedFile = null;
                } else if (selectedFile.exists()) {
                    final AlertDialog.Builder alert = new AlertDialog.Builder(this);

                    alert.setTitle(R.string.File_Diag_exists_Title);
                    alert.setMessage(getString(R.string.File_Diag_MSG_part).replace("%f", selectedFile.getName()));

                    alert.setPositiveButton(R.string.File_Diag_btn_OK, (dialog, whichButton) -> useFile());

                    alert.setNegativeButton(R.string.File_Diag_btn_CANCEL, (dialog, whichButton) -> selectedFile = null);
                    alert.show();
                } else {
                    useFile();
                }
            }

            if (!write && selectedFile != null) {
                useFile();
            }
        }
    }

    /**
     * finishes & returns file if selected
     */
    private void useFile() {
        if (selectedFile != null) {
            Intent returnIntent = new Intent();
            returnIntent.putExtra(RETURN_FILE, selectedFile);
            returnIntent.putExtra(RETURN_FILE_USER_NAME, tCurrentDir.getText().toString() + File.separator + selectedFile.getName());
            setResult(Activity.RESULT_OK, returnIntent);
            finish();
        }
    }

    /**
     * Checks current media state
     *
     * @return true when media is ready
     */
    private boolean checkMediaState() {
        String extState = Environment.getExternalStorageState();
        if (!extState.equals(Environment.MEDIA_MOUNTED) || extState.equals(Environment.MEDIA_MOUNTED_READ_ONLY)) {
            Log.e(TAG, "media state: " + extState);
            Toast.makeText(FileActivity.this, R.string.File_Error_Mediastate, Toast.LENGTH_LONG).show();
            return false;
        } else {
            return true;
        }
    }

    /**
     * Load default or last path / file into dialog
     */
    private void setBasicDir(SharedPreferences settings) {
        currentDir = new File(settings.getString(P_KEY_FA_LAST_DIR, ""));
        if (!currentDir.exists() || !currentDir.canWrite()) { // old value not valid anymore
            Log.w(TAG, "old path is invalid");
            currentDir = Environment.getExternalStorageDirectory();
        }
        this.basicDir = Environment.getExternalStorageDirectory().getAbsolutePath();
        tFileName.setText(settings.getString(P_KEY_FA_LAST_FILENAME, defaultFileName));
    }

    /**
     * Change directory in view to the one specified in currentDir<br>
     * if currentDir is null, we're assuming that the overview is required
     */
    private void changeDir() {
        selectedEntry = null;
        if (!write) {
            bOk.setEnabled(false);
        }
        if (checkMediaState()) {
            if (currentDir != null) {
                File[] files = currentDir.listFiles();
                if (files == null) {
                    Log.e(TAG, "null file list!");
                    Toast.makeText(FileActivity.this, R.string.File_Error_Nullpointer, Toast.LENGTH_LONG).show();
                    if(isRoot()){ // can't do anything, no access to base dir
                        return;
                    }
                    currentDir = currentDir.getParentFile();
                    files = currentDir.listFiles();
                }
                entries.clear();
                entries.add(new BasicFileEntry("..", "", 0, BasicFileEntry.TYPE_UP, true)); // go back entry
                for (File file : files) {
                    entries.add(new FileEntry(file, fmt));
                }
                applySorting();
            }
        }

        String newDirLabel = currentDir.getAbsolutePath().replaceFirst(basicDir, "");
        if (newDirLabel.length() == 0)
            newDirLabel = "/";
        tCurrentDir.setText(newDirLabel);
    }

    @Override
    protected void onStop() {
        super.onStop();
        // Save values
        SharedPreferences settings = getSharedPreferences(PREFS_NAME, 0);
        SharedPreferences.Editor editor = settings.edit();
        editor.putString(P_KEY_FA_LAST_FILENAME, tFileName.getText().toString());
        editor.putString(P_KEY_FA_LAST_DIR, currentDir.getAbsolutePath());
        editor.putBoolean(P_KEY_FA_SORT, sorting_name);
        editor.apply();
    }
}

